<?php	header('Content-Type: application/json');	include("../conexi.php");	$link=Conectarse();		$respuesta = array();			$consulta = "SELECT *  FROM ventas 		LEFT JOIN ventas_detalle USING(id_ventas)	LEFT JOIN productos USING(id_productos)		WHERE estatus_ventas = 'PENDIENTE'";		$result = mysqli_query( $link, $consulta ) 	or die("Error al ejecutar consulta: $consulta".mysqli_error($link));		$count_rows = mysqli_num_rows($result);	$count = 0;	while($row = mysqli_fetch_assoc($result)) {		$count++;		$respuesta["ventas"][] = $row ;			}		$respuesta["count_rows"] = "$count_rows" ;			$respuesta["mensaje"] =  $count_rows < 1 ? $mensaje_error : "OK";	$respuesta["encontrado"] =  $count_rows < 1 ? 0 : 1;	$respuesta["consulta"] =  $consulta;			print(json_encode($respuesta));	?>